import React, { useState } from "react";
import axios from "axios";

const Shortener = () => {
  const [url, setUrl] = useState("");
  const [shortUrl, setShortUrl] = useState("");

  const handleShorten = async () => {
    const res = await axios.post("http://localhost:5000/shorten", { originalUrl: url });
    setShortUrl(res.data.shortUrl);
  };

  return (
    <div>
      <input type="text" value={url} onChange={(e) => setUrl(e.target.value)} placeholder="Enter URL" />
      <button onClick={handleShorten}>Shorten</button>
      {shortUrl && <p>Short URL: <a href={shortUrl} target="_blank" rel="noreferrer">{shortUrl}</a></p>}
    </div>
  );
};

export default Shortener;
