import React, { useState } from "react";
import axios from "axios";

const API_BASE_URL = "http://localhost:5000/api";  // Updated to include /api

const ShortenForm = () => {
  const [originalUrl, setOriginalUrl] = useState("");
  const [shortened, setShortened] = useState("");
  const [qrCode, setQrCode] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleShorten = async () => {
    if (!originalUrl.trim()) {
      setError("Please enter a URL");
      return;
    }

    setLoading(true);
    setError("");

    try {
      // Normalize URL before sending
      let urlToSend = originalUrl.trim();
      if (!urlToSend.match(/^https?:\/\//)) {
        urlToSend = `http://${urlToSend}`;
      }

      const response = await axios.post(`${API_BASE_URL}/shorten`, { 
        originalUrl: urlToSend 
      });

      setShortened(response.data.shortUrl);
      setQrCode(response.data.qrCode);
    } catch (error) {
      console.error("Error:", error);
      setError(error.response?.data?.error || "Failed to shorten URL. Please try again.");
    } finally {
      setLoading(false);
    }
  };
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleShorten();
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>URL Shortener</h2>
      <input
        type="text"
        placeholder="Enter URL"
        value={originalUrl}
        onChange={(e) => setOriginalUrl(e.target.value)}
        onKeyPress={handleKeyPress}
        style={{ width: "300px", padding: "8px" }}
      />
      <button 
        onClick={handleShorten} 
        style={{ marginLeft: "10px", padding: "8px 12px" }}
        disabled={loading}
      >
        {loading ? "Shortening..." : "Shorten"}
      </button>

      {shortened && (
        <div style={{ marginTop: "20px" }}>
          <strong>Shortened URL:</strong>{" "}
          <a href={shortened} target="_blank" rel="noopener noreferrer">
            {shortened}
          </a>
          {qrCode && (
            <div style={{ marginTop: "10px" }}>
              <strong>QR Code:</strong><br />
              <img src={qrCode} alt="QR Code" style={{ marginTop: "10px", width: "150px", height: "150px" }} />
            </div>
          )}
        </div>
      )}

      {error && (
        <div style={{ marginTop: "20px", color: "red" }}>
          <strong>Error:</strong> {error}
        </div>
      )}
    </div>
  );
};

export default ShortenForm;
