import React, { useState } from "react";
import axios from "axios";

const ShortenForm = () => {
  const [originalUrl, setOriginalUrl] = useState("");
  const [shortened, setShortened] = useState("");
  const [error, setError] = useState("");

  const handleShorten = async () => {
    try {
      console.log("Sending URL:", originalUrl);

      const response = await axios.post("http://localhost:5000/api/shorten", {
        originalUrl,
      });

      console.log("Shortened URL:", response.data.shortUrl);
      setShortened(response.data.shortUrl);
      setError("");
    } catch (error) {
      console.error("Error:", error.response?.data || error.message);
      setShortened("");
      setError(error.response?.data?.error || "Something went wrong");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>URL Shortener</h2>
      <input
        type="text"
        placeholder="Enter URL"
        value={originalUrl}
        onChange={(e) => setOriginalUrl(e.target.value)}
        style={{ width: "300px", padding: "8px" }}
      />
      <button onClick={handleShorten} style={{ marginLeft: "10px", padding: "8px 12px" }}>
        Shorten
      </button>

      {shortened && (
        <div style={{ marginTop: "20px" }}>
          <strong>Shortened URL:</strong>{" "}
          <a href={shortened} target="_blank" rel="noopener noreferrer">
            {shortened}
          </a>
        </div>
      )}

      {error && (
        <div style={{ marginTop: "20px", color: "red" }}>
          <strong>Error:</strong> {error}
        </div>
      )}
    </div>
  );
};

export default ShortenForm;
